select * from (select rownum as rowidx, qry.* from (
select
v_bs_position_req.*, bs_position.p_name, bs_company.c_name, V_BUSI_HR.hr_name, V_BUSI_HR.age, V_BUSI_HR.sex_name, V_BUSI_HR.degree_name, V_BUSI_HR.jntc, V_BUSI_HR.hj_area_name
count(1) as record_count
from v_bs_position_req inner join bs_position on v_bs_position_req.P_ID = bs_position.P_ID inner join bs_company on bs_position.c_id = bs_company.c_id inner join V_BUSI_HR on V_BUSI_HR.hr_id = v_bs_position_req.HR_ID
v_bs_position_req.P_ID = #{P_ID}
and v_bs_position_req.HR_ID = #{HR_ID}
and bs_company.C_ID = #{C_ID}
and (bs_company.C_ID = #{owner_id} or bs_company.c_type = '4' and c_id_wt = #{owner_id})
order by v_bs_position_req.in_time desc
)qry ) where rowidx > (#{page} - 1) * #{rows} and rowidx <= #{page} * #{rows}

