mapper.select:

	select * from (select rownum as rowidx, qry.* from (

select 

	*


	count(1) as record_count

from FRAME_ACCOUNT 

	
		account =#{account}
	
	
		and account_kind =#{account_kind}
	
	
		and busi_id =#{busi_id}
	
	
		account_id =#{account_id}
	


	)qry ) where rowidx > (#{page} - 1) * #{rows} and   rowidx <= #{page} * #{rows}
